import { useState } from "react";

function TasksList({ list }) {
  const currentList = list.map((s, i) => <li key={i}>{s}</li>);
  return <ul>{currentList}</ul>
}


function Input({ onInput }) {
  return <input 
  className="input" 
  onKeyDown={e => { if (e.key === 'Enter') {
    onInput(e.target.value);
    e.target.value = '';
  }}}
  placeholder="+ Add task" 
  autoFocus />
}

export default function TodoList() {
  const [tasks, updateTasks] = useState(['Test task please work']);

  function handleInput(input) {
    const newTaskList = [...tasks, input];
    updateTasks(newTaskList);
  }

  return (
    <>
      <h1 className="title">Todo List</h1>
      <Input onInput={handleInput} />
      <TasksList list={tasks} />
      <button>Delete All</button>
    </>
  );
}
