import { useState } from "react";

function TaskButtons({ task, tasks, onEdit, onDelete }) {
  function handleEdit() {
    onEdit(task, prompt('edit', tasks[task]));
  }

  function handleDelete() {
    onDelete(task);
  }

  return <>
      <button onClick={handleEdit}>Edit</button>
      <button onClick={handleDelete}>Delete</button>
    </>
}

function TasksList({ list, onEdit, onDelete }) {
  const renderedList = list.map((s, i) => 
    <li key={i}>
      {s}
      <TaskButtons task={i} tasks={list} onEdit={onEdit} onDelete={onDelete} />
    </li>);
  return <ul>{renderedList}</ul>
}

function Input({ onInput }) {
  function handleEnter(e) {
    const value = e.target.value;
    if (e.key === 'Enter' && value.trim()) {
      onInput(value);
      e.target.value = '';
    }
  }

  function handleClick() {
    // to be implemented
    // onInput()
  }

  return <>
    <input 
      className="input" 
      maxLength={300}
      onKeyDown={e => handleEnter(e)}
      placeholder="+ Add task" 
      autoFocus
    />
    <button onClick={handleClick}>Add Todo</button>
  </>
}

export default function TodoList() {
  const [tasks, updateTasks] = useState([]);

  function handleInput(input) {
    const newTaskList = [...tasks, input];
    updateTasks(newTaskList);
  }

  function handleEdit(i, input) {
    const newTaskList = [...tasks];
    newTaskList[i] = input;
    updateTasks(newTaskList);
  }

  function handleDelete(i) {
    const newTaskList = [...tasks];
    newTaskList.splice(i, 1);
    updateTasks(newTaskList);
  }

  function handleDeleteAll() {
    updateTasks([]);
  }

  return (
    <>
      <h1 className="title">Todo List</h1>
      <Input onInput={handleInput} />
      <TasksList list={tasks} onEdit={handleEdit} onDelete={handleDelete} />
      <button onClick={handleDeleteAll}>Delete All</button>
    </>
  );
}
